config.xml preSubmitFunction 속성 관련 샘플 코드입니다
참고바랍니다.

gcm.PreSubmitFunction = function(submissionObj) {
    //현재 submission 의 ref로 전송할 요청데이터
    var reqData = WebSquare.ModelUtil.getRefToReqData( submissionObj );

    //요청데이터에 임의의 데이터 추가. 이 부분을 원하는 데이터로 가공하시면 됩니다. 
    var jsonObj = JSON.parse(reqData);
    for (var i in jsonObj) {
        if (jsonObj[i].length <= 0) {
            jsonObj[i] = [{"col1":"data is null"}];
        }
    }

    //submission 객체의 요청데이터에 최종적으로 추가
    submissionObj.requestData = jsonObj;
};

감사합니다

